import React, {useState} from 'react';
import { MDBAccordion, MDBAccordionItem, MDBBtn, MDBIcon } from 'mdb-react-ui-kit';

export default function App() {

    const [color1, setColor] = useState(false);

    var color2 = false;

    const btnColorChangeUp = () => {
        setColor(!color1)
    }


    const btnColorChangeDown = () => {
        setColor(!color2)
    }

  return (
    <MDBAccordion initialActive={1}>
      <MDBAccordionItem collapseId={1} headerTitle='Title 1'>
        <React.Fragment>
      <MDBBtn onClick={btnColorChangeUp} size='sm' floating style={ color ? { backgroundColor: 'Green' } : {backgroundColor: 'Blue'}}>
      <MDBIcon fas icon='arrow-up' />
    </MDBBtn>
    <MDBBtn onClick={btnColorChangeDown} size='sm' floating style={ color ? { backgroundColor: 'Red' } : {backgroundColor: 'Blue'}}>
      <MDBIcon fas icon='arrow-down' />
    </MDBBtn>
    </React.Fragment>
        </MDBAccordionItem>
      <MDBAccordionItem collapseId={2} headerTitle='Title 2'>
        Topic 2
      </MDBAccordionItem>
      <MDBAccordionItem collapseId={3} headerTitle='Title 3'>
        Topic 3
      </MDBAccordionItem>
    </MDBAccordion>
  );
}
