import React from 'react';
import { MDBAccordion, MDBAccordionItem, MDBBtn, MDBIcon } from 'mdb-react-ui-kit';

export default function App() {

    const [color, setColor] = useState(false);

  return (
    <MDBAccordion initialActive={1}>
      <MDBAccordionItem collapseId={1} headerTitle='Title 1'>
        <React.Fragment>
      <MDBBtn size='sm' floating style={{ backgroundColor: 'Blue' }}>
      <MDBIcon fas icon='arrow-up' />
    </MDBBtn>
    </React.Fragment>
        </MDBAccordionItem>
      <MDBAccordionItem collapseId={2} headerTitle='Title 2'>
        Topic 2
      </MDBAccordionItem>
      <MDBAccordionItem collapseId={3} headerTitle='Title 3'>
        Topic 3
      </MDBAccordionItem>
    </MDBAccordion>
  );
}
