import React, {useState} from 'react';
import { MDBAccordion, MDBAccordionItem, MDBBtn, MDBIcon } from 'mdb-react-ui-kit';

export default function App() {

    const [color, setColor] = useState(false);



    const btnColorChange = () => {
        setColor(!color)
    }


  return (
    <MDBAccordion>
      <MDBAccordionItem collapseId={1} headerTitle='Title 1' className='pb-5'>
        <React.Fragment>
      <MDBBtn className="float-end me-3 mb-2" onClick={btnColorChange} size='sm' floating style={ color ? { backgroundColor: 'Green' } : {backgroundColor: 'Blue'}} >
      <MDBIcon fas icon='arrow-up' />
    </MDBBtn>
    <MDBBtn className="float-end me-2 mb-2" onClick={btnColorChange} size='sm' floating style={ !color ? { backgroundColor: 'Red' } : {backgroundColor: 'Blue'}}>
      <MDBIcon fas icon='arrow-down' />
    </MDBBtn>
    </React.Fragment>
        </MDBAccordionItem>
    </MDBAccordion>
  );
}
